/*==============================================================*/
/* Table: CLASSIFICATION                                        */
/*==============================================================*/
create table CLASSIFICATION  (
   ID                   NUMBER(19)                      not null,
   WORD_ID              NUMBER(19)                      not null,
   CATEGORY_ID          NUMBER(10)                      not null,
   SEQUENCE             NUMBER(6)                       not null,
   CREATE_TIME          DATE,
   MODIFY_TIME          DATE,
   GRA_ID               NUMBER(2),
   constraint SYS_C0034424 primary key (ID)
);

comment on table CLASSIFICATION is
'单词教材映射表';

comment on column CLASSIFICATION.ID is
'流水号';

comment on column CLASSIFICATION.WORD_ID is
'单词ID';

comment on column CLASSIFICATION.CATEGORY_ID is
'教材ID';

comment on column CLASSIFICATION.SEQUENCE is
'单词在教材中的出现顺序';

comment on column CLASSIFICATION.CREATE_TIME is
'创建时间';

comment on column CLASSIFICATION.MODIFY_TIME is
'修改时间';

comment on column CLASSIFICATION.GRA_ID is
'年纪ID';

/*==============================================================*/
/* Index: IDX_CATEGORY_ID_WID                                   */
/*==============================================================*/
create index IDX_CATEGORY_ID_WID on CLASSIFICATION (
   CATEGORY_ID ASC,
   WORD_ID ASC
);

/*==============================================================*/
/* Index: IDX_CLASSFICATION_WID                                 */
/*==============================================================*/
create index IDX_CLASSFICATION_WID on CLASSIFICATION (
   WORD_ID ASC,
   GRA_ID ASC
);

alter table CLASSIFICATION
   add constraint FK_CLA_CATEGORY foreign key (CATEGORY_ID)
      references CATEGORY (ID)
      on delete cascade;

alter table CLASSIFICATION
   add constraint FK_CLA_WORD foreign key (WORD_ID)
      references WORD (ID)
      on delete cascade;


