/*==============================================================*/
/* Table: EDITION                                               */
/*==============================================================*/
create table EDITION  (
   ID                   NUMBER(3)                       not null,
   NAME                 VARCHAR2(200)                   not null,
   CREATE_TIME          DATE,
   MODIFY_TIME          DATE,
   UNIT_ENABLE          NUMBER(1),
   constraint PK_EDITION primary key (ID),
   constraint UQ_EDITION unique (NAME)
);

comment on table EDITION is
'单词版本信息表';

comment on column EDITION.ID is
'流水号';

comment on column EDITION.NAME is
'版本名称';

comment on column EDITION.CREATE_TIME is
'创建时间';

comment on column EDITION.MODIFY_TIME is
'修改时间';

comment on column EDITION.UNIT_ENABLE is
'单元练习是否可用';



